import style from "./Modal.module.css";
import { Fragment } from "react";
import PortalReactDOM from "react-dom"; //always use portalreactdom instead of just reactdom

const Backdrop = (props) => {
  return <div className={style.backdrop}>backdrop div</div>;
};

const ModalOverlay = (props) => {
  return (
    <div className={style.modal}>
      Modal div
      <div className={style.content}>{props.children}</div>
    </div>
  );
};

const portalElementID = document.getElementById("overlay-root");

const Modal = (props) => {
  <Fragment>
    {PortalReactDOM.createPortal(<Backdrop />, portalElementID)}
    {PortalReactDOM.createPortal(
      <ModalOverlay>{props.children}</ModalOverlay>,
      portalElementID
    )}
  </Fragment>;
};

export default Modal;
